import { View, Text, StyleSheet, Pressable, Image, Modal } from "react-native";
import { FlatGrid } from "react-native-super-grid";
import modalData from "../assets/data/cupons.json";
import React, { useState } from "react";

const CuponModal = (props) => {
  const [deleteButton, setDeleteButton] = useState(false);
  const showButton = () => {
    setDeleteButton(true);
  };
  return (
    <>
      <Modal animationType="slide" transparent={true} visible={props.visible}>
        <View style={styles.centeredViewInner}>
          {/* Top aouter View */}
          <View style={styles.qrCol1}>
            <Image
              source={{ uri: props.img }}
              style={{
                height: 150,
                width: 150,
                borderRadius: 5,
                marginTop: 10,
              }}
            />
            <View style={styles.data}>
              <Text style={styles.textData}>X9M75ZZ80</Text>
              <Text style={styles.textData}>{props.shop_name}</Text>
              <Text style={styles.textData}>:Categoria:</Text>
              <Text style={styles.textData}>{props.local}</Text>
              <Text style={styles.textData}>{props.token}</Text>
            </View>
          </View>
          {/* Items listing row */}
          <View>
            <FlatGrid
              data={modalData}
              itemDimension={50}
              spacing={20}
              style={{
                paddingBottom: 20,
                height: 300,
              }}
              keyExtractor={(item, index) => {
                return item.id;
              }}
              renderItem={(itemData) => {
                return (
                  <View style={{ flexDirection: "column" }}>
                    <Image
                      source={{ uri: itemData.item.img }}
                      style={styles.img}
                    />
                    <Text style={{ marginLeft: 10 }}>
                      $ {itemData.item.token}
                    </Text>
                  </View>
                );
              }}
            />
          </View>
          {/* Bottom button row*/}
          <View style={styles.buttonRow}>
            {deleteButton == false ? (
              <Pressable onPress={() => props.setVisible(false)}>
                <View
                  style={{
                    padding: 10,
                    width: 100,
                    borderWidth: 1,
                    borderColor: "#800020",
                    borderRadius: 5,
                    backgroundColor: "#800020",
                  }}
                >
                  <Text style={{ color: "#f5f5f5", textAlign: "center" }}>
                    Fechar
                  </Text>
                </View>
              </Pressable>
            ) : (
              <Pressable
                onPress={() => {
                  setDeleteButton(false);
                }}
              >
                <View
                  style={{
                    padding: 10,
                    width: 100,
                    height: 45,
                    backgroundColor: "#757575",
                    borderRadius: 5,
                  }}
                >
                  <Text style={{ color: "#f5f5f5", textAlign: "center" }}>
                    Cancelar
                  </Text>
                </View>
              </Pressable>
            )}

            {deleteButton == false ? (
              <Pressable onPress={() => showButton()}>
                <View>
                  <Text
                    style={{ textDecorationLine: "underline", padding: 10 }}
                  >
                    Deseja excluir cupom?
                  </Text>
                </View>
              </Pressable>
            ) : (
              <Pressable>
                <View
                  style={{
                    padding: 10,
                    width: 100,
                    borderWidth: 1,
                    borderColor: "#800020",
                    borderRadius: 5,
                    backgroundColor: "#800020",
                    color: "#f5f5f5",
                  }}
                >
                  <Text
                    style={{
                      color: "#f5f5f5",
                      textAlign: "center",
                    }}
                  >
                    Excluir
                  </Text>
                </View>
              </Pressable>
            )}
          </View>
        </View>
      </Modal>
    </>
  );
};

const styles = StyleSheet.create({
  centeredViewInner: {
    flex: 1,
    flexDirection: "column",
    borderRadius: 5,
    backgroundColor: "#f5f5f5",
    marginHorizontal: 10,
    marginBottom: 5,
    padding: 10,
    // borderWidth: 1,
    // borderColor: "#000000",
  },
  itemButton: {
    marginLeft: 5,
    backgroundColor: "#757575",
    borderWidth: 1,
    borderColor: "#9E9E9E",
    borderRadius: 5,
    padding: 10,
  },
  img: {
    height: 50,
    width: 50,
  },
  qrCol1: {
    justifyContent: "space-between",
    flexDirection: "row",
    // borderWidth: 1,
    // borderColor: "#000000",
  },
  listingRow: {
    justifyContent: "space-between",
    flexDirection: "row",
    flexWrap: "wrap",
    marginHorizontal: 10,
    marginTop: 50,
    paddingHorizontal: 20,
  },
  buttonRow: {
    marginTop: 50,
    justifyContent: "space-between",
    flexDirection: "row",
    paddingHorizontal: 10,
  },
  data: { flexWrap: "wrap", padding: 15 },
  textData: { lineHeight: 25 },
});

export default CuponModal;
