import { View, Text, StyleSheet, Pressable, Image, Modal } from "react-native";
import React, { useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome5";
import CuponModal from "./cupon_modal";
const Cupom = (props) => {
  const [visible, setVisible] = useState(false);
  return (
    <>
      <CuponModal
        visible={visible}
        setVisible={setVisible}
        img={props.img}
        shop_name={props.shop_name}
        local={props.local}
        token={props.token}
      />
      <View style={styles.innerContainer} key={props.id}>
        <View style={styles.col1}>
          <Image source={{ uri: props.img }} style={styles.img} />
          <View style={{ width: 200 }}>
            <Text style={{ marginTop: 40, color: "#BDBDBD" }}>
              Token expira em : 10 dias
            </Text>
          </View>
        </View>
        <View style={styles.col2}>
          <Text>Code</Text>
          <Text style={{ color: "#616161" }}>{props.shop_name}</Text>
          <Text style={{ color: "#9E9E9E" }}>{props.local}</Text>
        </View>
        <View style={styles.col3}>
          <Text style={styles.token}>
            <Icon name="horse-head" size={15} />
            {props.token}
          </Text>
          <Pressable onPress={() => setVisible(true)}>
            <View style={styles.itemButton}>
              <Text style={{ textAlign: "center", color: "#f5f5f5" }}>
                Ver itens
              </Text>
            </View>
          </Pressable>
        </View>
        <View style={styles.col4}>
          <Icon name="trash" size={25} />
        </View>
      </View>
    </>
  );
};

const styles = StyleSheet.create({
  innerContainer: {
    flexDirection: "row",
    padding: 5,
    marginBottom: 20,
    borderBottomWidth: 1,
    borderBottomColor: "#cccccc",
  },
  itemButton: {
    marginLeft: 5,
    backgroundColor: "#757575",
    borderWidth: 1,
    borderColor: "#9E9E9E",
    borderRadius: 5,
    padding: 10,
  },
  token: {
    marginLeft: 40,
    marginBottom: 10,
    color: "#000000",
    fontWeight: "bold",
    fontSize: 20,
  },
  img: {
    height: 50,
    width: 50,
  },
  col1: {
    flex: 1,
    marginRight: 10,
  },
  col2: {
    flex: 2,
  },
  col3: {
    flex: 2,
    flexDirection: "column",
  },
  col4: {
    marginTop: 45,
    marginLeft: 10,
  },
});

export default Cupom;
